                                                    document.addEventListener("DOMContentLoaded", function () {
                                                        document.querySelectorAll(".post-thumb").forEach(function (img) {
                                                            // 1. Cek apakah thumbnail kosong atau placeholder
                                                            if (!img.src || img.src.includes('placeholder.com')) {
                                                                const postOuter = img.closest(".post-outer");
                                                                if (postOuter) {
                                                                    const postContent = postOuter.querySelector(".post-snippet, .entry-content");
                                                                    if (postContent) {
                                                                        const firstImg = postContent.querySelector("img");
                                                                        if (firstImg) img.src = firstImg.src;
                                                                    }
                                                                }
                                                            }

                                                            // 2. Setelah gambar dimuat, hitung rasio & atur tinggi container
                                                            img.onload = function () {
                                                                const ratio = img.naturalWidth / img.naturalHeight;
                                                                const container = img.closest(".post-thumb-container");
                                                                if (container && ratio > 0) {
                                                                    container.style.height = (container.offsetWidth / ratio) + "px";
                                                                }
                                                            };
                                                        });
                                                    });

